function getManeuverFromActor(maneuver, actor) {
	return (actor.items.filter((item) => item.name === maneuver.name)[0] || null)
}

function isManeuver(item) {
    return (item.type==="pf1-pow.maneuver" && item.system.uses.value < item.system.uses.max && !item.system.disabled);
}

function getManeuvers(actor) {
    return (actor.items.filter(isManeuver))
}

function getManeuverOptions(maneuvers) {
    return (maneuvers.map(maneuver => maneuver.name))
}

var options = "";
try {
	var actor = game.actors.get(canvas.tokens.controlled[0].document.actorId);
} catch (error) {
	var actor = game.users.current.character;
	if (actor === null)
	{
		ui.notifications.error("Please select an actor token or pick a character in user configuration.");
		return ;
	}
}

getManeuverOptions(getManeuvers(actor)).forEach(maneuver => options += '<option value="' + maneuver + '">' + maneuver + '</option>');

const duoPartner = actor._rollData.duoPartner;

if (options === "")
{
    ui.notifications.error("You don't have any maneuvers to recover!");
    return ;
}

let d = new Dialog({
  title: 'Maneuver Recovery',
  content: `
    <form class="flexcol">
      <div class="form-group">
        <label for="maneuverSelect">Select a maneuver</label>
        <select name="maneuverSelect">` + options + `
        </select>
      </div>
    </form>
  `,
  buttons: {
    yes: {
      icon: '<i class="fas fa-check"></i>',
      label: 'Use maneuver',
      callback: (html) => {
        let select = html.find('[name="maneuverSelect"]').val();
        getManeuvers(actor).forEach(maneuver => {
        if (maneuver.name === select)
	{
		maneuver.update({'system.uses.value': maneuver.system.uses.value + 1});
		if (duoPartner)
		{
			let partnerManeuver = getManeuverFromActor(maneuver, duoPartner);
			if (partnerManeuver)
				partnerManeuver.update({'system.uses.value': maneuver.system.uses.value + 1});
		}

		ui.notifications.info("Recovered " + maneuver.name + ".");
	}
    })
      }
    },
  },
  default: 'yes'
}).render(true)
